function getCurrentPrice() {
  const priceElement = document.querySelector('#priceblock_ourprice, #priceblock_dealprice, .a-price .a-offscreen');
  if (!priceElement) return null;  
  const price = parseFloat(priceElement.textContent.replace(/[^0-9,.]/g, '').replace(',', '.'));
  return price;
}

function getASIN() {
  const match = window.location.pathname.match(/\/dp\/([A-Z0-9]{10})/);
  return match ? match[1] : null;
}

// When the page loads
document.addEventListener('DOMContentLoaded', () => {
  const asin = getASIN();
  const currentPrice = getCurrentPrice();
  
  if (asin) {
    chrome.runtime.sendMessage({
      type: 'COMPARE_PRICES',
      productId: asin,
      currentPrice: currentPrice
    }, response => {
      if (response.success) {
        // Handle the response here
        displayResults(response.data);
      } else {
        console.error('Price comparison failed:', response.error);
      }
    });
  }
});

function displayResults(results) {
  if (!results || results.length === 0) {
    // Show "No better prices found" only if we really checked all marketplaces
    showTooltip('No better prices found at the moment. Current marketplace has the best price.');
    return;
  }

  const betterPrices = results.filter(r => r.savings > 0);
  if (betterPrices.length > 0) {
    const bestDeal = betterPrices[0];
    showTooltip(`Better price found! Save ${bestDeal.savings}% on ${bestDeal.marketplace}`);
  }
}